import React from 'react';

const Box = ({ isRunning, duration, theme }) => {
  const boxStyle = {
    width: '200px',
    height: '200px',
    border: '2px solid #000',
    position: 'relative',
    margin: '20px auto',
  };

  const dotStyle = {
    width: '20px',
    height: '20px',
    backgroundColor: 'red',
    borderRadius: '50%',
    position: 'absolute',
    top: '0',
    left: '0',
  };

  const phaseTextStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    fontSize: '18px',
  };

  const countdownStyle = {
    position: 'absolute',
    bottom: '10px',
    right: '10px',
    fontSize: '24px',
  };

  return (
    <div className="box-container">
      <div style={boxStyle}>
        <div style={dotStyle}></div>
        <div style={phaseTextStyle}>Inhale</div>
        <div style={countdownStyle}>{duration}</div>
      </div>
    </div>
  );
};

export default Box;
