import React, { useState, useEffect, useRef } from 'react';
import Controls from './Controls';
import Box from './Box';
import Summary from './Summary';
// import './BoxBreathing.css';

const BoxBreathing = () => {
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [duration, setDuration] = useState(4);
  const [theme, setTheme] = useState('default');
  const [music, setMusic] = useState('none');
  const [breathCount, setBreathCount] = useState(0);
  const [showSummary, setShowSummary] = useState(false);
  const [totalTime, setTotalTime] = useState(0);
  const [startTime, setStartTime] = useState(null);

  const startTimeRef = useRef(null);
  const pauseTimeRef = useRef(null);

  useEffect(() => {
    if (isRunning && !isPaused) {
      setStartTime(performance.now());
      startTimeRef.current = performance.now();
    } else {
      setStartTime(null);
    }
  }, [isRunning, isPaused]);

  const handleStart = () => {
    if (isPaused) {
      const now = performance.now();
      setStartTime(now - pauseTimeRef.current);
      startTimeRef.current = now - pauseTimeRef.current;
      setIsPaused(false);
    } else {
      setStartTime(performance.now());
      startTimeRef.current = performance.now();
      setBreathCount(0);
    }
    setIsRunning(true);
    setShowSummary(false); // Hide summary when starting
  };

  const handlePause = () => {
    pauseTimeRef.current = performance.now() - startTimeRef.current;
    setIsPaused(true);
  };

  const handleStop = () => {
    if (isRunning || isPaused) {
      setIsRunning(false);
      setIsPaused(false);
      setTotalTime(Math.floor((performance.now() - startTimeRef.current) / 1000));
      setStartTime(null);
      startTimeRef.current = null;
      setShowSummary(true); // Show summary only when stopping
      setDuration(prevDuration => prevDuration);
    }
  };

  const handleMute = () => {
    setIsMuted(!isMuted);
    // Implement actual audio muting here
  };

  return (
    <div className="box-breathing">
      <Box
        isRunning={isRunning}
        duration={duration}
        theme={theme}
        startTime={startTime}
        key={startTime}
      />
      <Controls
        isRunning={isRunning}
        isPaused={isPaused}
        isMuted={isMuted}
        onStart={handleStart}
        onPause={handlePause}
        onStop={handleStop}
        onMute={handleMute}
        duration={duration}
        setDuration={setDuration}
        theme={theme}
        setTheme={setTheme}
        music={music}
        setMusic={setMusic}
      />
      {showSummary && (
        <Summary
          totalTime={totalTime}
          breathCount={breathCount}
          onClose={() => setShowSummary(false)}
        />
      )}
    </div>
  );
};

export default BoxBreathing;
