import React, { useEffect, useRef } from 'react';

const Box = ({ isRunning, duration, theme, startTime }) => {
  const boxRef = useRef(null);
  const dotRef = useRef(null);
  const phaseTextRef = useRef(null);
  const countdownRef = useRef(null);

  const phases = ['Exhale', 'Hold', 'Inhale', 'Hold'];
  const points = [
    { x: 300, y: 0 },   // A: Upper right
    { x: 300, y: 300 }, // B: Lower right
    { x: 0, y: 300 },   // C: Lower left
    { x: 0, y: 0 }      // D: Upper left
  ];

  useEffect(() => {
    if (isRunning && startTime) {
      const animate = (timestamp) => {
        const elapsedTime = timestamp - startTime;
        const totalCycleDuration = duration * 4 * 1000;
        const cycleTime = elapsedTime % totalCycleDuration;
        const phaseIndex = Math.floor(cycleTime / (duration * 1000));
        const phaseTime = cycleTime % (duration * 1000);

        // Calculate position
        const startPoint = points[phaseIndex];
        const endPoint = points[(phaseIndex + 1) % 4];
        const progress = phaseTime / (duration * 1000);

        // Apply 0.2-second pause at each point
        const pauseDuration = 200; // 0.2 seconds in milliseconds
        let adjustedProgress = progress;
        if (progress < pauseDuration / (duration * 1000)) {
          adjustedProgress = 0;
        } else if (progress > 1 - pauseDuration / (duration * 1000)) {
          adjustedProgress = 1;
        } else {
          adjustedProgress = (progress - pauseDuration / (duration * 1000)) / (1 - 2 * pauseDuration / (duration * 1000));
        }

        const x = startPoint.x + (endPoint.x - startPoint.x) * adjustedProgress;
        const y = startPoint.y + (endPoint.y - startPoint.y) * adjustedProgress;

        if (dotRef.current) {
          dotRef.current.style.left = `${x - 13}px`;
          dotRef.current.style.top = `${y - 13}px`;
        }

        // Update phase text
        if (phaseTextRef.current) {
          phaseTextRef.current.textContent = phases[phaseIndex];
        }

        // Update countdown
        if (countdownRef.current) {
          const countdown = Math.max(0, Math.ceil((duration * 1000 - phaseTime) / 1000));
          countdownRef.current.textContent = countdown;
        }

        requestAnimationFrame(animate);
      };

      requestAnimationFrame(animate);
    }
  }, [isRunning, duration, theme, startTime]);

  const boxContainerStyle = {
    position: 'relative',
    width: '300px',
    height: '300px',
    margin: '20px auto',
  };

  const boxStyle = {
    width: '100%',
    height: '100%',
    border: '2px solid #000',
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  };

  const dotStyle = {
    width: '26px',
    height: '26px',
    borderRadius: '50%',
    position: 'absolute',
    top: '-13px',
    right: '-13px',
  };

  const phaseTextStyle = {
    fontSize: '24px',
    opacity: 1,
    transition: 'opacity 0.3s ease-in-out',
    position: 'absolute',
    top: '40%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '100%',
    textAlign: 'center',
  };

  const countdownStyle = {
    fontSize: '18px',
    color: 'rgba(0, 0, 0, 0.5)',
    position: 'absolute',
    bottom: '40%',
    left: '50%',
    transform: 'translateX(-50%)',
    width: '100%',
    textAlign: 'center',
  };

  return (
    <div style={boxContainerStyle}>
      <div ref={boxRef} style={boxStyle}>
        <div ref={dotRef} style={dotStyle}></div>
        <div ref={phaseTextRef} style={phaseTextStyle}></div>
        <div ref={countdownRef} style={countdownStyle}></div>
      </div>
    </div>
  );
};

export default Box;
