import React, { useEffect, useRef } from 'react';

const Box = ({ isRunning, duration, theme }) => {
  const boxRef = useRef(null);
  const dotRef = useRef(null);
  const phaseTextRef = useRef(null);
  const countdownRef = useRef(null);

  const boxContainerStyle = {
    position: 'relative',
    width: '300px',
    height: '300px',
    margin: '20px auto',
  };

  const boxStyle = {
    width: '100%',
    height: '100%',
    border: '2px solid #000',
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  };

  const dotStyle = {
    width: '26px',
    height: '26px',
    borderRadius: '50%',
    position: 'absolute',
    top: '-13px',
    right: '-13px',
  };

  const phaseTextStyle = {
    fontSize: '24px',
    opacity: 1,
    transition: 'opacity 0.3s ease-in-out',
    position: 'absolute',
    top: '40%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '100%',
    textAlign: 'center',
  };

  const countdownStyle = {
    fontSize: '18px',
    color: 'rgba(0, 0, 0, 0.5)',
    position: 'absolute',
    bottom: '40%',
    left: '50%',
    transform: 'translateX(-50%)',
    width: '100%',
    textAlign: 'center',
  };

  useEffect(() => {
    if (isRunning) {
      // Animation logic will go here
    }
  }, [isRunning, duration, theme]);

  return (
    <div style={boxContainerStyle}>
      <div ref={boxRef} style={boxStyle}>
        <div ref={dotRef} style={dotStyle}></div>
        <div ref={phaseTextRef} style={phaseTextStyle}></div>
        <div ref={countdownRef} style={countdownStyle}></div>
      </div>
    </div>
  );
};

export default Box;
